	b->yy_ch_buf = (char *) amp_ac_yyalloc(b->yy_buf_size + 2 ,yyscanner );
	if ( ! b->yy_ch_buf )
		YY_FATAL_ERROR( "out of dynamic memory in amp_ac_yy_create_buffer()" );

	b->yy_is_our_buffer = 1;

	amp_ac_yy_init_buffer(b,file ,yyscanner);

	return b;
